SELECT *
FROM CEN_CUST_TX cct
  LEFT JOIN CEN_CUST_TX_ATTRDTL ccta ON ccta.tx_serno=cct.tx_serno
WHERE CATEGORY_CD IN ('CAT_A');

SELECT *
FROM CEN_CUST_TX cct
  LEFT JOIN CEN_CUST_TX_ATTRDTL ccta ON ccta.tx_serno=cct.tx_serno WHERE cct.CATEGORY_CD IN ('CAT_A', 'CAT B', 'CAT_C', 'CAT_D', 'EBP', 'ECC', 'CAT_A_FIRST', 'CAT_B_FIRST', 'CAT_C_FIRST', 'CAT_D_FIRST', 'ECC_FIRST') AND cct.CREATE_DT >= '01/01/2022' AND cct.CREATE_DT <= '12/31/2023' AND cct.CUST_ID IN ('BOC389684','405458','406151','406544','BOC234901','BOC327419','BOC335615','BOC336644','BOC359946','BOC365473','BOC378514','BOC383232','BOC388209','BOC391160','BOC294419','403443','405717','405852','405903','407085','408724','029902500','030802040','079702455','BOC182723','BOC220416','BOC220924','BOC240401','BOC251502','BOC266733','BOC267109','BOC270101','BOC270804');